rule.definition:
/*Get the full name of user*/
= if(
  #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
    topeople(
      ri!User
    )
  ),
  " ",
  if(
    isusernametaken(
      ri!User
    ),
    if(
      and(
        not(
          #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
            ri!User
          )
        ),
        isuseractive(
          ri!User
        )
      ),
      user(
        touser(
          ri!User
        ),
        "firstName"
      ) & " " & user(
        touser(
          ri!User
        ),
        "lastName"
      ),
      tostring(
        ri!User
      )
    ),
    if(
      or(
        #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
          tointeger(ri!User)
        ),
        not(
          doesgroupexist(
            tointeger(ri!User)
          )
        )
      ),
      {},
      group(
        tointeger(ri!User),
        "groupName"
      )
    )
  )
)
