rule.definition:
with(
  local!subString1: split(
    ri!Email, "@"
  ),
  local!subString2: if(
    or(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!subString1),
      length(local!subString1)<=1
    ),
    null,
    split(local!subString1[2], ".")
  ),
  local!string1: if(
    or(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!subString1),
      length(local!subString1)<=1
    ),
    null,
    local!subString1[1]
  ),
  local!string2: if(
    or(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!subString2),
      length(local!subString2)<=1,
      length(local!subString2)>3
    ),
    null,
    local!subString2[1]
  ),
  local!string3: if(
    or(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!subString2),
      length(local!subString2)<=1,
      length(local!subString2)>3
    ),
    null,
    local!subString2[2]
  ),
  local!string4: if(
    and(
      not(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!subString2)),
      length(local!subString2)=3
    ),
    local!subString2[3],
    null
  ),
  if(
    or(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!string1),
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!string2),
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!string3)
    ),
    false(),
    {
      if(
        and(
          count(local!subString2)=3,
          #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!string4)
        ),
        false(),
        {
          if(
            length(local!subString2)=3,
            {
              if(
                and(
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string1,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 1
                    )
                  ),
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string2,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 2
                    )
                  ),
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string3,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 2
                    )
                  ),
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string4,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 2
                    )
                  )
                ),
                true(),
                false()
              )
            },
            {
              if(
                and(
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string1,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 1
                    )
                  ),
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string2,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 2
                    )
                  ),
                  #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221859"(
                    textToCheck: local!string3,
                    invalidCharacters: #"_a-0000e4dd-0d8b-8000-5257-1bef901bef90_221758"(
                      input: 2
                    )
                  )
                ),
                true(),
                false()
              )
            }
          )
        }
      )
    }
  )
)
