interface.definition:
a!localVariables(
  local!workFlowSteps: #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_7827"(
    categoryGroup: "WORKFLOW",
    type: "WORKFLOW_STATUS",
    isActive: true(),
