      local!workFlowSteps
    ),
    remove(
      local!workFlowSteps,
      wherecontains(
        "APP_CHCK",
        touniformstring(
          index(
            local!workFlowSteps,
            "code",
            null
          )
        )
      )
    ).label,
    {}
  ),
  local!removeCheckerRejectStep: if(
    #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9906"(
      local!workFlowSteps
    ),
    remove(
      local!workFlowSteps,
      wherecontains(
        "REJ_CHCK",
        touniformstring(
          index(
            local!workFlowSteps,
            "code",
            null
          )
        )
      )
    ).label,
    {}
  ),
  {
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      label: "",
      value: #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: if(
          toboolean(
            ri!isChecker
          ),
          "Checker Review - ",
          "Maker Review - "
        ) & ri!processDetails.name & " - " & ri!requestDetails.requestId,
        color: "ACCENT",
        size: "MEDIUM_PLUS",
        style: "STRONG"
      )
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_milestoneField"(
          label: "Status",
          steps: if(
            ri!requestDetails.requestCurrStatus = "REJ_CHCK",
            local!removeCheckerApproverStep,
            local!removeCheckerRejectStep
