rule.definition:
with(
  local!details: #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9924"(
    processId: ri!processId
  ),
  local!makers: #"SYSTEM_SYSRULES_forEach"(
    items: split(
      index(
        local!details,
        "makers",
        {}
      ),
      ";"
    ),
    expression: trim(
      fv!item
    )
  ),
  local!checkers: #"SYSTEM_SYSRULES_forEach"(
    items: split(
      index(
        local!details,
        "checkers",
        {}
      ),
      ";"
    ),
    expression: trim(
      fv!item
    )
  ),
  local!doaUsers: #"SYSTEM_SYSRULES_forEach"(
    items: split(
      index(
        local!details,
        "doaUsers",
        {}
      ),
      ";"
    ),
    expression: trim(
      fv!item
    )
  ),
  local!finalMakers: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      ri!checkerId
    ),
    local!makers,
    remove(
      local!makers,
      wherecontains(
        tostring(
          ri!checkerId
        ),
        touniformstring(
          local!makers
        )
      )
    )
  ),
  local!finalCheckers: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      ri!makerId
    ),
    local!checkers,
    remove(
      local!checkers,
      wherecontains(
        tostring(
          ri!makerId
        ),
        touniformstring(
          local!checkers
        )
      )
    )
  ),
  local!finalDOAUsers: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      ri!checkerId
    ),
    local!doaUsers,
    remove(
      local!doaUsers,
      wherecontains(
        {
          tostring(
            ri!makerId
          ),
          tostring(
            ri!checkerId
          )
        },
        touniformstring(
          local!doaUsers
        )
      )
    )
  ),
  if(
    ri!isMakerBl,
    #"SYSTEM_SYSRULES_forEach"(
      items: local!finalMakers,
      expression: touser(
        trim(
          fv!item
        )
      )
    ),
    if(
      ri!isCheckerBl,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!finalCheckers,
        expression: touser(
          trim(
            fv!item
          )
        )
      ),
      if(
        ri!IsDOABI,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!finalDOAUsers,
          expression: touser(
            trim(
              fv!item
            )
          )
        ),
        null
      )
    )
  )
)
