rule.definition:
#"SYSTEM_SYSRULES_gridRowLayout"(
  contents: {
    /* Recurrence Type */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_textField"(
        value: index(
          ri!runConfig[ri!index],
          "recurrence_type",
          {}
        ),
        readOnly: ri!isReadOnly
      ),
      #"SYSTEM_SYSRULES_dropdownField"(
        placeholderLabel: "--Select recurrence type--",
        required: true(),
        choiceLabels: #"_a-0000e4c6-e892-8000-5254-1bef901bef90_22735",
        choiceValues: #"_a-0000e4c6-e892-8000-5254-1bef901bef90_22735",
        value: index(
          ri!runConfig[ri!index],
          "recurrence_type",
          {}
        ),
        saveInto: ri!runConfig[ri!index].recurrence_type
      )
    ),
    /* Processing Month */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_textField"(
        value: index(
          ri!runConfig[ri!index],
          "processing_month",
          {}
        ),
        readOnly: ri!isReadOnly
      ),
      #"SYSTEM_SYSRULES_dropdownField"(
        placeholderLabel: "--Select processing month--",
        required: true(),
        choiceLabels: #"_a-0000e4c6-4628-8000-5253-1bef901bef90_18824",
        choiceValues: #"_a-0000e4c6-4628-8000-5253-1bef901bef90_18824",
        value: index(
          ri!runConfig[ri!index],
          "processing_month",
          {}
        ),
        saveInto: ri!runConfig[ri!index].processing_month
      )
    ),
    /* Processing Weekdays */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_textField"(
        value: index(
          ri!runConfig[ri!index],
          "processing_weekdays",
          {}
        ),
        readOnly: ri!isReadOnly
      ),
      #"SYSTEM_SYSRULES_dropdownField"(
        placeholderLabel: "--Select processing Week day--",
        required: true(),
        choiceLabels: #"_a-0000e4c6-4628-8000-5253-1bef901bef90_21023",
        choiceValues: #"_a-0000e4c6-4628-8000-5253-1bef901bef90_21023",
        value: index(
          ri!runConfig[ri!index],
          "processing_weekdays",
          {}
        ),
        saveInto: ri!runConfig[ri!index].processing_weekdays
      )
    ),
    /* Recurrence Frequency */
    #"SYSTEM_SYSRULES_integerField"(
      label: "Recurrence Frequency",
      readOnly: ri!isReadOnly,
      value: index(
        ri!runConfig[ri!index],
        "recurrence_frequency",
        {}
      ),
      required: true(),
      saveInto: a!save(
        ri!runConfig[ri!index].recurrence_frequency,
        save!value
      ),
      align: "RIGHT"
    ),
    /* Trigger Time */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: todatetime(
            index(
              ri!runConfig[ri!index],
              "trigger_time",
              {}
            )
          )
        ),

      ),
      #"SYSTEM_SYSRULES_dateTimeField"(
        required: true(),
        value: index(
          ri!runConfig[ri!index],
          "trigger_time",
          {}
        ),
        saveInto: a!save(
          ri!runConfig[ri!index].trigger_time,
          save!value
        ),
        align: "RIGHT"
      )
    ),
    /* Next Trigger Time */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: todatetime(
            index(
              ri!runConfig[ri!index],
              "next_trigger_datetime",
              {}
            )
          )
        ),

      ),
      #"SYSTEM_SYSRULES_dateTimeField"(
        required: true(),
        value: index(
          ri!runConfig[ri!index],
          "next_trigger_datetime",
          {}
        ),
        validations: if(
          ri!runConfig[ri!index].next_trigger_datetime <= ri!runConfig[ri!index].trigger_time,
          "Invalid Date",
          {}
        ),
        saveInto: ri!runConfig[ri!index].next_trigger_datetime,
        align: "RIGHT"
      )
    ),
    /* Is Active */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      #"SYSTEM_SYSRULES_textField"(
        readOnly: ri!isReadOnly,
        required: true(),
        value: if(
          index(
            ri!runConfig[ri!index],
            "is_active",
            {}
          ) = 1,
          "Active",
          "In-active"
        )
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        align: "CENTER",
        value: {
          if(
            or(
              tointeger(
                index(
                  ri!runConfig[ri!index],
                  "is_active",
                  {}
                )
              ) = 1
            ),
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "toggle-on",
              caption: "Active",
              linkStyle: "STANDALONE",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                value: 1,
                saveInto: a!save(
                  ri!runConfig[ri!index].is_active,
                  0
                )
              ),
              size: "MEDIUM_PLUS"
            ),
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "toggle-off",
              caption: "In-active",
              linkStyle: "STANDALONE",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                value: 0,
                saveInto: a!save(
                  ri!runConfig[ri!index].is_active,
                  1
                )
              ),
              size: "MEDIUM_PLUS"
            )
          )
        }
      )
    ),
    /* Edit Option */
    if(
      contains(
        ri!isReadOnly,
        true()
      ),
      {},
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        align: "CENTER",
        value: {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "edit",
            caption: "Edit",
            linkStyle: "STANDALONE",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: 1,
              saveInto: a!save(
                ri!runConfigId,
                ri!runConfig[ri!index].run_config_id
              )
            ),
            size: "MEDIUM_PLUS"
          ),

        }
      )
    )
  }
)
