interface.definition:
a!localVariables(
  local!selectedEntitySheetMapping: index(
    ri!entityFieldMapping,
    wherecontains(
      tointeger(
        ri!selectedEntityMappingId
      ),
      tointeger(
        index(
          ri!entityFieldMapping,
          "uiEntityMappingId",
          null
        )
      )
    ),
    null
  ),
  {
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: #"SYSTEM_SYSRULES_forEach"(
        items: ri!entityMapping,
        expression: #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: fv!item.displayName,
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: {
                  a!save(
                    ri!selectedEntityName,
                    fv!item.displayName
                  ),
                  a!save(
                    ri!selectedEntityMappingId,
                    fv!item.uiEntityMappingId
                  ),
                  a!save(
                    ri!gridDisplayType,
                    fv!item.displayType
                  ),
                  a!save(
                    ri!data,
                    {
                      {
                        GRP: 1,
                        BRANCH: "SYB",
                        AMOUNT: 123,
                        dateOpened: now()
                      },
                      {
                        GRP: 2,
                        BRANCH: "STD",
                        AMOUNT: 1234,
                        dateOpened: now()
                      }
                    }
                  )
                }
              )
            )
          ),
          width: "MINIMIZE"
        )
      )
    ),
    #"SYSTEM_SYSRULES_gridField_v1"(
      showWhen: ri!gridDisplayType = "READONLY_GRID",
      label: ri!selectedEntityName & " Data",
      emptyGridMessage: "No Rows present for " & ri!selectedEntityName & " Data",
      data: todatasubset(
        ri!data,
        fv!pagingInfo
      ),
      columns: #"SYSTEM_SYSRULES_forEach"(
        items: local!selectedEntitySheetMapping,
        expression: #"SYSTEM_SYSRULES_gridColumn"(
          label: fv!item.displayName,
          value: if(
            fv!item.displayType = "DATE",
            if(
              #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9906"(
                index(
                  fv!row,
                  fv!item.entityColumnName,
                  null
                )
              ),
              text(
                index(
                  fv!row,
                  fv!item.entityColumnName,
                  null
                ),
                if(
                  #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9906"(
                    fv!item.displayFormat
                  ),
                  fv!item.displayFormat,
                  "MM/DD/YYYY"
                )
              ),
              index(
                fv!row,
                fv!item.entityColumnName,
                null
              )
            ),
            if(
              fv!item.displayType = "DOLLAR",
              dollar(
                index(
                  fv!row,
                  fv!item.entityColumnName,
                  0
                ),
                if(
                  #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9906"(
                    fv!item.decimalPoints
                  ),
                  fv!item.decimalPoints,
                  2
                )
              ),
              index(
                fv!row,
                fv!item.entityColumnName,
                null
              )
            )
          )
        )
      ),
      pageSize: 100
    ),
    #"SYSTEM_SYSRULES_gridLayout"(
      showWhen: ri!gridDisplayType = "EDITABLE_GRID",
      label: ri!selectedEntityName & " Data",
      emptyGridMessage: "No Rows present for " & ri!selectedEntityName & " Data",
      headerCells: #"SYSTEM_SYSRULES_forEach"(
        items: local!selectedEntitySheetMapping,
        expression: #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: fv!item.displayName,
          align: "CENTER"
        )
      ),
      columnConfigs: #"SYSTEM_SYSRULES_forEach"(
        items: local!selectedEntitySheetMapping,
        expression: #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
          width: "DISTRIBUTE"
        )
      ),
      rows: #"SYSTEM_SYSRULES_forEach"(
        items: ri!data,
        expression: load(
          local!currentIndex: fv!index,
          local!currentRow: fv!item,
          #"SYSTEM_SYSRULES_gridRowLayout"(
            contents: #"SYSTEM_SYSRULES_forEach"(
              items: local!selectedEntitySheetMapping,
              expression: #"SYSTEM_SYSRULES_textField"(
                value: index(
                  local!currentRow,
                  fv!item.entityColumnName,
                  null
                ),
                saveInto: {
                  a!save(
                    ri!data[local!currentIndex],
                    updatedictionary(
                      local!currentRow,
                      #"SYSTEM_SYSRULES_fromJson_v1"(
                        substitute(
                          #"SYSTEM_SYSRULES_toJson_v1"(
                            {
                              replaceText: save!value
                            }
                          ),
                          "replaceText",
                          fv!item.entityColumnName
                        )
                      )
                    )
                  )
                }
              )
            )
          )
        )
      )
    )
  }
)

a:ruleInputTestConfigs.a:value:
{
  {
    displayName:"006",
    uiEntityMappingId:1,
    displayType:"EDITABLE_GRID"
  },
  {
    displayName:"Pivot",
    uiEntityMappingId:2,
    displayType:"READONLY_GRID"
  }
}
