interface.definition:
a!localVariables(
  local!paramId,
  local!depDetails:#"_a-0000e4c6-e892-8000-5254-1bef901bef90_57426"(
    dependentParameterId:ri!depParaId
  ),
  {#"SYSTEM_SYSRULES_gridField_v1"(
  label:"",
  data: ri!refIntSysDepParam,
  columns:{
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Parameter Label",
      sortField: "parameter_label",
      value: fv!row.parameter_label,
      align:"START"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Parameter Code",
      sortField: "parameter_code",
      value: fv!row.parameter_code,
      align:"START"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Generation Type",
      sortField: "generation_type",
      value: fv!row.generation_type,
      align:"START"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Parameter Type",
      sortField: "parameter_type",
      value: fv!row.parameter_type,
      align:"START"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Master Ref Data Type",
      sortField: "master_refdata_type",
      value: fv!row.master_refdata_type,
      align:"START"
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"Edit Details",
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: #"SYSTEM_SYSRULES_richTextIcon"(
              icon:"edit",
              size:"MEDIUM",
              link:#"SYSTEM_SYSRULES_dynamicLink"(
                value:"Edit",
                saveInto: {
                  a!save(
                    ri!editDetails,
                    save!value
                  ),
                  a!save(
                    ri!refIntSysDepParamEdit,
                   #"_a-0000e4c6-e892-8000-5254-1bef901bef90_57426"(
                     systemParameterId:ri!systemParameterId
                   )
                  )
                },
                showWhen: if(
                  or(
                    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(ri!editDetails)
                  ),
                  true(),
                  false()
                )
              ),
              color: if(
                or(
                  #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(ri!editDetails)
                ),
                "ACCENT",
                "SECONDARY"
                )
            )
          ),
        }
      ),
      align:"CENTER",
      showWhen: ri!readOnly<>true()
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label:"View Details",
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: if(
              tointeger(fv!row.dependent_parameter_id)
              =tointeger(ri!depParaId),
              {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "eye-slash",
                  caption: "Close"
                ),
              },
              {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "eye",
                  caption: "View"
                ),
              }
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: if(
                tointeger(fv!row.dependent_parameter_id)
                =tointeger(ri!depParaId),
                null,
                tointeger(fv!row.dependent_parameter_id)
              ),
              saveInto: {
                a!save(
                  ri!depParaId,
                  save!value
                )

              }
            )
          )
        }
      ),
      align:"CENTER",
      showWhen:ri!readOnly=true()
    )
  },
  pageSize: 5,
  emptyGridMessage:"No data available"
),
if(
  and(ri!readOnly=true(),not(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(ri!depParaId))),
  #"_a-0000e4cd-1af4-8000-5255-1bef901bef90_143061"(
    refIntSysDepParam: local!depDetails,

    isReadOnly: true(),

    systemParameterId: ri!systemParameterId
  ),
  {}
)
})
