interface.definition:
load(
  local!count: count(#"_a-0000e4c6-e892-8000-5254-1bef901bef90_57426"(
    systemParameterId: ri!systemParameterId
  )),
  local!refIntSysParam: #"_a-0000e4c6-e892-8000-5254-1bef901bef90_57331"(
    systemParameterId: ri!systemParameterId
  ),
  local!dependentParameterlabel: concat(
    touniformstring(
      index(local!refIntSysParam, "lookup_system_type_code", {})
    ),
    "|",
    touniformstring(
      index(local!refIntSysParam, "parameter_label", {}))
  ),
  local!intSysDepParam: #"_a-0000e4c6-e892-8000-5254-1bef901bef90_57426"(
    systemParameterId: ri!systemParameterId,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: -1,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "parameter_label",
      ascending: true()
    )
  )
  ),
  local!dependentParameterId: index(local!intSysDepParam[1], "dependent_parameter_id", {}),
  local!dependentParameter: local!intSysDepParam[1],
  {
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_milestoneField"(
    steps: {"System Parameter", "System Dependent Parameter"},
    active: 2,
  ),
    if(
      tointeger(local!count)>1,
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_forEach"(
                items: enumerate(local!count)+1,
                expression: {
                  #"SYSTEM_SYSRULES_cardLayout"(
                  contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: index(local!intSysDepParam[fv!item], "parameter_label", {}),
                      style: "STRONG"
                    ),
                    align: "CENTER"
                  ),
                  link: #"SYSTEM_SYSRULES_dynamicLink"(
                    value: index(local!intSysDepParam[fv!item], "dependent_parameter_id", {}),
                    saveInto: {
                      a!save(local!dependentParameterId, save!value),
                      a!save(
                        local!dependentParameter,
                        local!intSysDepParam[fv!item]
                      )
                    }
                  ),
                  style: if(
                    tointeger(local!dependentParameterId)=
                    tointeger(
                      index(local!intSysDepParam[fv!item], "dependent_parameter_id", {})
                      ),
                    "STANDARD",
                    "INFO"
                    )
                ),
                #"SYSTEM_SYSRULES_richTextDisplayField"()
                }
              )
            },
            width: "NARROW"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"_a-0000e4cd-1af4-8000-5255-1bef901bef90_143061"(
              refIntSysDepParam: local!dependentParameter,
              isReadOnly: true,
              dependentParameter: local!dependentParameterlabel
            )
          )
        }
      ),
      #"_a-0000e4cd-1af4-8000-5255-1bef901bef90_143061"(
        refIntSysDepParam: local!intSysDepParam,
        isReadOnly: true,
        dependentParameter: local!dependentParameterlabel
      )
    )
    }
    )
  }
)
