rule.definition:
#"SYSTEM_SYSRULES_gridRowLayout"(
  contents: {

    /*mapping_value*/
    #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_285963"(
      value: index(
        ri!CustomGeneration[ri!index],
        "custom_generator_name",
        {}
      ),
      placeholder:"--Enter custom generator name--",
      saveInto: {

        a!save(
          ri!CustomGeneration[ri!index].custom_generator_name,
          trim(
            save!value
          )
        ),
        a!save(
          ri!CustomGeneration[ri!index].modified_by,
          loggedInUser()
        ),
        a!save(
          ri!CustomGeneration[ri!index].modified_on,
          now()
        )
      },
      readOnly: if(
        ri!isReadOnly=true(),
        true(),
        false()
      ),
      required: if(
        ri!isReadOnly=true(),
        false(),true()
      )
    ),

    /*isactive*/
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      align: "CENTER",
      value: {
        if(
          or(
            tointeger(
              index(
                ri!CustomGeneration[ri!index],
                "is_active",
                {}
              )
            ) = 1
          ),
          #"SYSTEM_SYSRULES_richTextIcon"(
            linkStyle: "STANDALONE",
            icon: "toggle-on",
            caption: "Yes",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: 1,
              showWhen: if(
                or(
                  ri!isReadOnly
                ),
                false(),
                true()
              ),
              saveInto: {a!save(
                ri!CustomGeneration[ri!index].is_active,
                0
              ),
              a!save(
                ri!CustomGeneration[ri!index].modified_by,
                loggedInUser()
              ),
              a!save(
                ri!CustomGeneration[ri!index].modified_on,
                now()
              )
              },
            ),
            size: "MEDIUM_PLUS",
            color: "ACCENT"
          ),
          #"SYSTEM_SYSRULES_richTextIcon"(
            linkStyle: "STANDALONE",
            icon: "toggle-off",
            caption: "No",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: 0,
              showWhen: if(
                or(
                  ri!isReadOnly
                ),
                false(),
                true()
              ),
              saveInto: {a!save(
                ri!CustomGeneration[ri!index].is_active,
                1
              ),

              a!save(
                ri!CustomGeneration[ri!index].modified_by,
                loggedInUser()
              ),
              a!save(
                ri!CustomGeneration[ri!index].modified_on,
                now()
              )}
            ),
            size: "MEDIUM_PLUS",
            color: "ACCENT"
          )
        )
      }
    ),
    /*remove*/
    if(
      or(
        not(
          #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
            index(
              ri!CustomGeneration[ri!index],
              "custom_generation_id",
              {}
            )
          )
        ),
        count(
          ri!CustomGeneration
        ) = 1
      ),
      if(
        ri!isReadOnly = true(),
        {},
        #"SYSTEM_SYSRULES_richTextDisplayField"()
      ),
      if(
        ri!isReadonly = true(),
        {},
        #"SYSTEM_SYSRULES_imageField_v1"(
          label: "delete ",
          images: #"SYSTEM_SYSRULES_documentImage"(
            document: #"SYSTEM_SYSRULES_iconIndicator"(
              "REMOVE"
            ),
            altText: "Remove",
            caption: "Remove",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: ri!index,
              saveInto: {
                if(
                  #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
                    index(
                      ri!CustomGeneration[ri!index],
                      "custom_generation_id",
                      {}
                    )
                  ),
                  a!save(
                    ri!CustomGeneration,
                    remove(
                      ri!CustomGeneration,
                      save!value
                    )
                  ),
                  a!save(
                    index(
                      ri!CustomGeneration[ri!index],
                      "is_active",
                      {}
                    ),
                    0
                  )
                )
              }
            )
          ),
          size: "ICON"
        )
      )
    )
  }
)
