rule.definition:
a!localVariables(

  local!escalation: #"_a-0000e4cd-1af4-8000-5255-1bef901bef90_70017"(
    configId: ri!ioConfigId,
    isActive: true()
  ),
  local!hrs: if(
    ri!dateTime>=now(),
    elapsedhours(now(), ri!dateTime),
    elapsedhours(ri!dateTime, now())
    ),
    local!days: if(
      ri!dateTime>=now(),
      days360(today(), todate(ri!dateTime)),
      days360(todate(ri!dateTime), today())
      ),
  local!slaPeriodDay: index(
    local!escalation, 
    wherecontains(
      #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_308191"[2], 
      touniformstring(
        index(local!escalation, "sla_timer_type",{})
        )
    ),
    {}
  ),
  local!slaPeriodHrs: index(
    local!escalation, 
    wherecontains(
      #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_308191"[1], 
      touniformstring(
        index(local!escalation, "sla_timer_type",{})
        )
    ),
    {}
  ),
  local!dayEscalation: #"SYSTEM_SYSRULES_forEach"(
    items: local!slaPeriodDay,
    expression: if(
      tointeger(fv!item.sla_period)<=tointeger(local!days),
      fv!item,
      null
    )
  ),
  local!hrsEscalation: #"SYSTEM_SYSRULES_forEach"(
    items: local!slaPeriodHrs,
    expression: if(
      tointeger(fv!item.sla_period)<=tointeger(local!hrs),
      fv!item,
      null
    )
  ),
  {
    remove(local!hrsEscalation, wherecontains(null, touniformstring(local!hrsEscalation))),
    remove(local!dayEscalation, wherecontains(null, touniformstring(local!dayEscalation)))
  }
)
