rule.definition:
a!localVariables(
  local!refProcess: if(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
    ri!calendarId
  ),{},
  #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_12194"(
    calendarId: ri!calendarId
  )),
  local!runconfigs:
  if(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(index(local!refProcess,"process_id",{})),
  {},#"_a-0000e4c3-ab68-8000-5250-1bef901bef90_11827"(
    processId:index(local!refProcess,"process_id",{})
  )),
  local!masterLookup: 
  if(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(index(local!runconfigs,"processing_weekdays",{})),
  {},#"_a-0000e4c3-ab68-8000-5250-1bef901bef90_7827"(
    categoryGroup: #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_10373"[3],
    type: #"_a-0000e4cd-1af4-8000-5255-1bef901bef90_108743"[2],
    label:  split(index(local!runconfigs,"processing_weekdays",{}),",")

  )),
  local!processingWeekdays: index(local!masterLookup,"code",{}),
  if(or(
    contains(
      tointeger(local!processingWeekdays),
      tointeger(ri!nonWorkingDay)
    )),
    "This day can not be selected as non working day because it is selected as processing weekday in run configuration. Please modify the run configuration first.",
    "")
)
