rule.definition:
a!localVariables(
  local!data,

  with(
    #"SYSTEM_SYSRULES_gridRowLayout"(

      contents: {


        #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_285963"(
          label: "Version Description",
          placeholder:"--Enter Version Description--",
          value:index(ri!refProcessWFTemplateVersions[ri!index],"version_description",{}),
          saveInto: ri!refProcessWFTemplateVersions[ri!index].version_description,
          readOnly:or(
            ri!isReadOnly
          ),
          required: true(),
          validations: {
            if(
              ri!isReadOnly,
              {},
              #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3251505"(
                value: index(ri!refProcessWFTemplateVersions[ri!index],"version_description",{}),
                ValidationLength: #"_a-0000e4c6-e892-8000-5254-1bef901bef90_42536"[1]
              )
            )
          }
        ),
        if(
          or(
            ri!isDocumentIdExist = true(),
            ri!isReadOnly=true(),
            not(
              #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
                index(ri!refProcessWFTemplateVersions[ri!index],"document_version_id",{})))
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: extract( 
                getcontentdetailsbyuuid(
                  index(ri!refProcessWFTemplateVersions[ri!index],"appian_document_id",{})
                ),
                "Name:",
                ","
              )             

            /*  document(
                index(ri!refProcessWFTemplateVersions[ri!index],"appian_document_id",{}),"name"
              )  */ ,
                link: #"SYSTEM_SYSRULES_documentDownloadLink"(
                  document: /* todocument(index(ri!refProcessWFTemplateVersions[ri!index],"appian_document_id",{})) */
                  todocument(
                    stripwith(
                      index(
                        split(
                          getcontentdetailsbyuuid(
                            index(ri!refProcessWFTemplateVersions[ri!index],"appian_document_id",{})
                          ),
                          ","
                        ),2,{}),
                        "Id: ")
                  )

                )
            )
          ),

          #"SYSTEM_SYSRULES_fileUploadField_v1"(
            label:    "Upload Template" ,
            target:#"_a-0000e4c6-4628-8000-5253-1bef901bef90_17394",
            maxSelections:1,
            value:ri!documentId ,
            saveInto: {
              ri!documentId
              /*,
              if(
                rule!EAP_Utils_CheckIsNull(ri!documentId),
                {},
              a!save(
                ri!refProcessWFTemplateVersions[ri!index].appian_document_id ,
                tostring(
                  index(
                    extract(
                      getcontentobjectdetailsbyid(
                       ri!documentId
                      ),
                      "UUID:",
                      ","
                    ) ,
                    1,
                    {}
                  )
                )
              )

              )
             */
              },
            readOnly:ri!isReadOnly,
            validations:if(
            fv!files.size<1024,
            "File size should not be less than 1kb",
           {}
            ),
            required: true()
          )
        )

        ,


        if(
          or(ri!isReadOnly=true()),
          #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_285963"(
            readOnly: ri!isReadOnly,
            required: true(),
            value: if(
              index(
                ri!refProcessWFTemplateVersions[ri!index],
                "isactive",
                {}
              ) = 1,
              "Yes",
              "No"
            )
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            align: "CENTER",
            value: {if(
              or(
                tointeger(
                  index(
                    ri!refProcessWFTemplateVersions[ri!index],
                    "isactive",
                    {}
                  )
                ) = 1
              ),
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "toggle-on",
                caption: "Yes",
                link:#"SYSTEM_SYSRULES_dynamicLink"(
                  value:1,
                  saveInto: {a!save(  
                    ri!refProcessWFTemplateVersions[ri!index].isactive,0),
                  if(#"_a-0000e5c3-a65f-8000-5268-1bef901bef90_767759"=true,
                  a!save(
                    ri!dwfProcessControllerData,append(
                      ri!dwfProcessControllerData,#"_a-0000e5c3-a65f-8000-5268-1bef901bef90_756090"(
                        dataValidationId:ri!templateConfigId
                      )
                    )
                  ),{})}
                ),
                linkStyle:"STANDALONE",
                size: "MEDIUM_PLUS"
              ),
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "toggle-off",
                caption: "No",
                linkStyle:"STANDALONE",
                link:#"SYSTEM_SYSRULES_dynamicLink"(
                  value:0,
                  saveInto: {a!save(  
                    ri!refProcessWFTemplateVersions[ri!index].isactive,1),if(#"_a-0000e5c3-a65f-8000-5268-1bef901bef90_767759"=true,
                    a!save(
                      ri!dwfProcessControllerData,difference(
                        ri!dwfProcessControllerData,#"_a-0000e5c3-a65f-8000-5268-1bef901bef90_756090"(
                          dataValidationId:ri!templateConfigId
                        )
                      )
                    ),{})}
                ),
                size: "MEDIUM_PLUS"
              )
            )}


          )

        ),

        if(
          or(
            ri!isReadonly=true(),
            count(ri!refProcessWFTemplateVersions)<2
          ),
          {},
          #"SYSTEM_SYSRULES_imageField_v1"(
            label: " ",
            images: #"SYSTEM_SYSRULES_documentImage"(
              document: #"SYSTEM_SYSRULES_iconIndicator"(
                icon: if(
                  ri!index = 1 ,
                  "MOVE_UP_DISABLED",
                  "MOVE_UP"
                )
              ),
              link: if(
                ri!index = 1 ,
                {},
                #"SYSTEM_SYSRULES_dynamicLink"(
                  saveInto: {
                    a!save(
                      local!data,
                      ri!refProcessWFTemplateVersions[ri!index-1]
                    ),
                    a!save(
                      ri!refProcessWFTemplateVersions[ri!index-1],
                      ri!refProcessWFTemplateVersions[ri!index]
                    ),
                    a!save(
                      ri!refProcessWFTemplateVersions[ri!index],
                      local!data
                    ),
                    #"SYSTEM_SYSRULES_forEach"(
                      items: ri!refProcessWFTemplateVersions,
                      expression: 
                      a!save(
                        ri!refProcessTempConfig[fv!index].sequence_order,
                        fv!index
                      )
                    )
                  }
                )
              )
            )
          )
        ),
        if(
          or(
            ri!isReadonly=true(),
            count(ri!refProcessWFTemplateVersions)<2
          ),
          {},
          #"SYSTEM_SYSRULES_imageField_v1"(
            images: #"SYSTEM_SYSRULES_documentImage"(
              document: #"SYSTEM_SYSRULES_iconIndicator"(
                if(
                  or(
                    length(ri!refProcessWFTemplateVersions)=1,
                    ri!index = count(ri!refProcessWFTemplateVersions) 
                  ) ,
                  "MOVE_DOWN_DISABLED",
                  "MOVE_DOWN"


                )

              ),
              link:   #"SYSTEM_SYSRULES_dynamicLink"(
                showWhen: if(ri!index = count(ri!refProcessWFTemplateVersions),
                false(),true()
                ),
                saveInto: {
                  a!save(
                    local!data,
                    ri!refProcessWFTemplateVersions[ri!index]
                  ),

                  a!save(
                    ri!refProcessWFTemplateVersions[ri!index],
                    ri!refProcessWFTemplateVersions[ri!index+1],
                  ),
                  a!save(
                    ri!refProcessWFTemplateVersions[ri!index+1],
                    local!data
                  ),
                  #"SYSTEM_SYSRULES_forEach"(
                    items: ri!refProcessWFTemplateVersions,
                    expression:  a!save(ri!refProcessTempConfig[fv!index].sequence_order,
                    fv!index
                    )
                  )
                }
              )
            ) 
          )),
          if(
            or(ri!isReadOnly=true(),count(ri!refProcessWFTemplateVersions)=1),
            {},
            #"SYSTEM_SYSRULES_imageField_v1"(


              label: "delete ",
              images: #"SYSTEM_SYSRULES_documentImage"(
                document: #"SYSTEM_SYSRULES_iconIndicator"(
                  "REMOVE"
                ),
                altText: "Remove Document",
                caption: "Remove",
                link: #"SYSTEM_SYSRULES_dynamicLink"(
                  value: ri!index,
                  saveInto: {
                    if(
                      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
                        index(ri!refProcessWFTemplateVersions[ri!index],"document_version_id",{})
                      ),
                      a!save(
                        ri!refProcessWFTemplateVersions,
                        remove(
                          ri!refProcessWFTemplateVersions,
                          save!value
                        )
                      ),
                      a!save(
                        index(ri!refProcessWFTemplateVersions[ri!index],"isactive",{}),
                        false()
                      )
                    )

                  }
                )
              ),
              size: "ICON"
            )
          )

      }
    )
  )
)
