rule.definition:
with(
  local!backwardArrowShow_bool: tointeger(
    ri!pagingInfo.startIndex
  ) > 1,
  local!forwardArrowShow_bool: tointeger(
    ri!pagingInfo.startIndex + (
      ri!batchSize - 1
    )
  ) < tointeger(
    ri!totalCount
  ),
  local!doubleArrowShow_bool: (
    ri!totalCount / ri!batchSize > 2.0
  ),
  local!textToDisplay_txt: ri!pagingInfo.startIndex & if(
    ri!currentDataCount > 1,
    " " & char(
      6150
    ) & " " & if(
      tointeger(
        ri!pagingInfo.startIndex + (
          ri!batchSize - 1
        )
      ) <= tointeger(
        ri!totalCount
      ),
      ri!pagingInfo.startIndex + (
        ri!batchSize - 1
      ),
      tointeger(
        ri!totalCount
      )
    ),
    ""
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    labelPosition: "COLLAPSED",
    align: ri!align,
    value: {
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "angle-double-left",
        linkStyle: "STANDALONE",
        size: "MEDIUM_PLUS",
        link: if(
          or(
            local!backwardArrowShow_bool
          ),
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: 1,
            saveInto: {
              ri!pagingInfo.startIndex,
              ri!saveList
            }
          ),
          ""
        ),
        caption: if(
          or(
            local!backwardArrowShow_bool
          ),
          property(
            ri!bundleData,
            "First",
            "First"
          ) & " " & property(
            ri!bundleData,
            "Page",
            "Page"
          ),
          ""
        ),
        showWhen: and(
          or(
            local!doubleArrowShow_bool
          ),
          or(
            local!backwardArrowShow_bool
          )
        )
      ),
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "angle-left",
        linkStyle: "STANDALONE",
        size: "MEDIUM_PLUS",
        link: if(
          or(
            local!backwardArrowShow_bool
          ),
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: ri!pagingInfo.startIndex - ri!batchSize,
            saveInto: {
              ri!pagingInfo.startIndex,
              ri!saveList
            }
          ),
          ""
        ),
        caption: if(
          or(
            local!backwardArrowShow_bool
          ),
          property(
            ri!bundleData,
            "Previous",
            "Previous"
          ) & " " & property(
            ri!bundleData,
            "Page",
            "Page"
          ),
          ""
        ),
        showWhen: or(
          local!backwardArrowShow_bool
        )
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!textToDisplay_txt,
        style: "STRONG",
        size: "MEDIUM"
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        size: "MEDIUM",
        text: " of " & if(#"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"( ri!totalCount),null,index(
          split(
            dollar(
              ri!totalCount,
              0
            ),
            "$"
          ),
          2,
          {}
        ))
      ),
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "angle-right",
        linkStyle: "STANDALONE",
        size: "MEDIUM_PLUS",
        link: if(
          or(
            local!forwardArrowShow_bool
          ),
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: ri!pagingInfo.startIndex + ri!batchSize,
            saveInto: {
              ri!pagingInfo.startIndex,
              ri!saveList
            }
          ),
          ""
        ),
        caption: if(
          or(
            local!forwardArrowShow_bool
          ),
          property(
            ri!bundleData,
            "Next",
            "Next"
          ) & " " & property(
            ri!bundleData,
            "Page",
            "Page"
          ),
          ""
        ),
        showWhen: or(
          local!forwardArrowShow_bool
        )
      ),
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "angle-double-right",
        linkStyle: "STANDALONE",
        size: "MEDIUM_PLUS",
        link: if(
          or(
            local!forwardArrowShow_bool
          ),
          #"SYSTEM_SYSRULES_dynamicLink"(
            value: (
              ri!totalCount - if(
                mod(
                  ri!totalCount,
                  ri!batchSize
                ) = 0,
                ri!batchSize,
                mod(
                  ri!totalCount,
                  ri!batchSize
                )
              )
            ) + 1,
            saveInto: {
              ri!pagingInfo.startIndex,
              ri!saveList
            }
          ),
          ""
        ),
        caption: if(
          or(
            local!forwardArrowShow_bool
          ),
          property(
            ri!bundleData,
            "Last",
            "Last"
          ) & " " & property(
            ri!bundleData,
            "Page",
            "Page"
          ),
          ""
        ),
        showWhen: and(
          or(
            local!doubleArrowShow_bool
          ),
          or(
            local!forwardArrowShow_bool
          )
        )
      )
    }
  )
)
