rule.definition:
#"SYSTEM_SYSRULES_gridRowLayout"(
  id: ri!index,
  contents: {
    /*System Type Code*/
    #"SYSTEM_SYSRULES_textField"(
      label: "System Type Code",
      value: index(ri!refIntSys[ri!index],"system_type_code",{}),
      readOnly: true()
    ),
    /*Is Active*/
    if(
      or(
        tointeger(
          index(ri!refIntSys[ri!index],"is_available",{})
        )=1
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        label: "Is Available",
        value:  #"SYSTEM_SYSRULES_richTextIcon"(
          linkStyle: "STANDALONE",
          icon: "toggle-on",
          caption: "Yes",
          color: "POSITIVE",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: 1,
            saveInto: {
              a!save(
                ri!refIntSys[ri!index].is_available,
                0
              )

            },

          ),
          size: "MEDIUM_PLUS"
        )
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        label: "Is Active",
        value:  #"SYSTEM_SYSRULES_richTextIcon"(
          linkStyle: "STANDALONE",
          icon: "toggle-off",
          color: "NEGATIVE",
          caption: "No",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: 0,
            saveInto: {
              a!save(
                ri!refIntSys[ri!index].is_available,
                1
              ),

            },

          ),
          size: "MEDIUM_PLUS"
        ))



    )
  }
)
