interface.definition:
a!localVariables(
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    align: "CENTER",
    value: {
      if(
        or(
          ri!value
        ) = or(
          ri!expectedValue
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          color: "",
          text: if(
            or(
              ri!value
            ),
            "Yes",
            "No"
          )
        ),
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            color: "NEGATIVE",
            text: concat(
              if(
                or(
                  ri!value
                ),
                "Yes",
                "No"
              )
            )
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            color: "POSITIVE",
            text: concat(
              " (",
              if(
                or(
                  ri!expectedValue
                ),
                "Yes",
                "No"
              ),
              ")"
            )
          )
        }
      )
    }
  )
)
