rule.definition:
with(
  /*SOE ID*/
  local!soeId: index(
    index(
      ri!emailContact,
      wherecontains(
        "SOEID",
        index(
          ri!emailContact,
          "recipient_type",
          null
        )
      ),
      null
    ),
    "recipient",
    null
  ),
  local!soeIdArray: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!soeId
    ),
    null,
    split(
      local!soeId,
      ";"
    )
  ),
  local!soeIdAddress: #"SYSTEM_SYSRULES_forEach"(
    items: local!soeIdArray,
    expression: if(
      isusernametaken(
        trim(
          fv!item
        )
      ),
      user(
        trim(
          fv!item
        ),
        "email"
      ),
      null
    )
  ),
  /*Group UUID */
  local!groupUUID: index(
    index(
      ri!emailContact,
      wherecontains(
        "RECIPIENT_GROUP",
        index(
          ri!emailContact,
          "recipient_type",
          null
        )
      ),
      null
    ),
    "recipient",
    null
  ),
  local!groupUUIDArray: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!groupUUID
    ),
    null,
    split(
      local!groupUUID,
      ";"
    )
  ),
  local!groupId: #"SYSTEM_SYSRULES_forEach"(
    items: local!groupUUIDArray,
    expression: #"_a-0000e4e8-bfcb-8000-5258-1bef901bef90_256415"(
      inputUUID: trim(
        fv!item
      )
    )
  ),
  local!groupMembers: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!groupId
    ),
    null,
    #"SYSTEM_SYSRULES_forEach"(
      items: local!groupId,
      expression: a!groupMembers(
        fv!item
      ).data
    )
  ),
  local!groupMembertext: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!groupMembers
    ),
    null,
    joinarray(
      local!groupMembers,
      ";"
    )
  ),
  local!groupMemberArray: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!groupMembertext
    ),
    null,
    split(
      local!groupMembertext,
      ";"
    )
  ),
  local!groupUser: #"SYSTEM_SYSRULES_forEach"(
    items: union(
      local!groupMemberArray,
      local!groupMemberArray
    ),
    expression: touser(
      fv!item
    )
  ),
  local!groupAddress: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!groupUser
    ),
    null,
    #"SYSTEM_SYSRULES_forEach"(
      items: local!groupUser,
      expression: if(
        isusernametaken(
          trim(
            fv!item
          )
        ),
        user(
          trim(
            fv!item
          ),
          "email"
        ),
        null
      )
    )
  ),
  /*Recipient*/
  local!recipient: index(
    index(
      ri!emailContact,
      wherecontains(
        "RECIPIENT",
        index(
          ri!emailContact,
          "recipient_type",
          null
        )
      ),
      null
    ),
    "recipient",
    null
  ),
  local!recipientArray: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!recipient
    ),
    null,
    split(
      local!recipient,
      ";"
    )
  ),
  local!recipientAddress: #"SYSTEM_SYSRULES_forEach"(
    items: local!recipientArray,
    expression: trim(
      fv!item
    )
  ),
  /*Maker Checker*/
  local!makerBl: contains(
    index(
      ri!emailContact,
      "recipient_type",
      null
    ),
    "MAKER"
  ),
  local!checkerBl: contains(
    index(
      ri!emailContact,
      "recipient_type",
      null
    ),
    "CHECKER"
  ),
  local!makerCheckerUserName: if(
    or(
      local!makerBl,
      local!checkerBl
    ),
    #"_a-0000e4c3-ab68-8000-5250-1bef901bef90_9960"(
      processId: ri!processId,
      isMakerBl: local!makerBl,
      isCheckerBl: local!checkerBl
    ),
    null
  ),
  local!makerCheckerAddress: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      local!makerCheckerUserName
    ),
    null,
    #"SYSTEM_SYSRULES_forEach"(
      items: local!makerCheckerUserName,
      expression: user(
        fv!item,
        "email"
      )
    )
  ),
  local!address: {
    local!soeIdAddress,
    local!recipientAddress,
    local!groupAddress,
    local!makerCheckerAddress
  },
  local!unionAddress: union(
    local!address,
    local!address
  ),
  reject(
    fn!isnull,
    local!unionAddress
  )
)
