rule.definition:
if(
  /*check is field type is NUMBER*/
  and(
    index(
      ri!EntityFieldMapping,
      "fieldtype",
      {}
    ) = "NUMBER",
    tointeger(
      index(
        ri!EntityFieldMapping,
        "decimalCount",
        null
      )
    ) > 0
  ),
  if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
      index(
        ri!data,
        index(
          ri!EntityFieldMapping,
          "entityColumnName",
          {}
        ),
        {}
      )
    ),
    {},
    fixed(
      index(
        ri!data,
        index(
          ri!EntityFieldMapping,
          "entityColumnName",
          {}
        ),
        {}
      ),
      2,
      true()
    )
  ),
  if(
    /*check is field type is DATE_FORMAT*/
    or( index(
      ri!EntityFieldMapping,
      "fieldtype",
      {}
    ) = "DATE_FORMAT"),
    if(
      #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(
        index(
          ri!data,
          index(
            ri!EntityFieldMapping,
            "entityColumnName",
            {}
          ),
          {}
        )
      ),
      {},
    concat(
      todate(
        index(
      ri!data,
      index(
        ri!EntityFieldMapping,
        "entityColumnName",
        {}
      ),
      {}
        ))," ",totime(index(
          ri!data,
          index(
            ri!EntityFieldMapping,
            "entityColumnName",
            {}
          ),
          {}
        )))),
        /*check is field type is text*/
    tostring(
      index(
        ri!data,
        index(
          ri!EntityFieldMapping,
          "entityColumnName",
          {}
        ),
        {}
      )
    )
  )

)
