rule.definition:
a!localVariables(

  local!controllerEntries:if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(ri!processControllerMasterId),{},
    #"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_361812"(
      processControllerMasterId: ri!processControllerMasterId
    )
  ),
  local!makerStepIds: if(
    #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!controllerEntries),{},
    #"SYSTEM_SYSRULES_forEach"(
      items:local!controllerEntries,
      expression: {
        if(
          tointeger(fv!item.milestone_id) = tointeger(#"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_365703"[5]),
          fv!index,
          {}
        )
      }
    )
  ),  

 local!previousMakerStepId: if(
    contains(
      tointeger(local!makerStepIds),
      tointeger(ri!stepId)
    ),
    index(
      local!makerStepIds,
      wherecontains(
        ri!stepId,
        local!makerStepIds
      )-1,
      {}
    ),
    {}
  ),
  /*local!previousMakerStepId,*/     
 {
   if(
     or(
       #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(local!previousMakerStepId),
       #"_a-0000e4b2-6487-8000-2b63-01ef9001ef90_3233095"(ri!stepId)
     ),
     {},
     contains(
       tointeger(
         index(
           if(
             or(local!previousMakerStepId = ri!stepId),
             #"SYSTEM_SYSRULES_forEach"(
               items: local!controllerEntries,
               expression: 
               if(
                 and(
                   fv!index>=1,
                   fv!index<=ri!stepId
                 ),
                 fv!item,
                 {}
               )

             ),      
             #"SYSTEM_SYSRULES_forEach"(
               items: local!controllerEntries,
               expression: 
               if(
                 and(
                   fv!index>=local!previousMakerStepId,
                   fv!index<=ri!stepId
                 ),
                 fv!item,
                 {}
               )

             )

           ),
           "milestone_id",
           {}
         )
       ),
       tointeger(#"_a-0000e4e9-ef8d-8000-5259-1bef901bef90_365703"[11])

     )     

   ),  


 }
)
