interface.definition:
#"SYSTEM_SYSRULES_richTextDisplayField"(
  value: {
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-double-left",
      size: "MEDIUM",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(ri!pagingInfo.startIndex, 1),
          ri!save
        }
      ),
      showWhen: ri!pagingInfo.startIndex > 1
    ),
    " ",
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-left",
      size: "MEDIUM",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            ri!pagingInfo.startIndex - ri!pagingInfo.batchSize
          ),
          ri!save
        }
      ),
      showWhen: not(ri!pagingInfo.startIndex <= 1)
    ),
    " ",
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      style: "STRONG",
      text: if(
        ri!totalCount > 0,
        { ri!pagingInfo.startIndex, " - " },
        ""
      ),

    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      style: "STRONG",
      text: if(
        ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 > ri!totalCount,
        ri!totalCount,
        ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1
      )
    ),
    if(ri!totalCount > 0, " of ", ""),
    #"SYSTEM_SYSRULES_richTextItem_v1"(style: "STRONG", text: ri!totalCount),
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-right",
      size: "MEDIUM",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            ri!pagingInfo.startIndex + ri!pagingInfo.batchSize
          ),
          ri!save
        }
      ),
      showWhen: ri!totalCount >= ri!pagingInfo.startIndex + ri!pagingInfo.batchSize
    ),
    " ",
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-double-right",
      size: "MEDIUM",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            if(
              mod(ri!totalCount, ri!pagingInfo.batchSize) = 0,
              ri!totalCount - ri!pagingInfo.batchSize + 1,
              ri!totalCount - mod(ri!totalCount, ri!pagingInfo.batchSize) + 1
            )
          ),
          ri!save
        }
      ),
      showWhen: not(
        ri!pagingInfo.startIndex + ri!pagingInfo.batchSize >= ri!totalCount
      )
    ),

  },
  align: "RIGHT"
)
